    is_valid, message = validate_dataframe(cleaned_df, ['A', 'B', 'C'])
    print(f"Validation: {is_valid} - {message}")
    
    summary = get_data_summary(cleaned_df)
    print(f"Number of columns: {len(summary['columns'])}")